import react, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [Date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [transactions,setTransactions] = useState([])



useEffect(()=>{
  getTransactions().then(transactions => {
    setTransactions(transactions);
  })
},[])


async function getTransactions() {
  const res = await fetch("http://localhost:5000/api/transactions")
  return await res.json();
}

  const addNewTransaction = async (e) =>{
    e.preventDefault();
    const price = name.split(' ')[0]
    // const url=process.env.REACT_APP_URI
    // console.log(url)
    const res = await fetch("http://localhost:5000/api/transaction",{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify({price,name:name.substring(price.length+1),Date,description})

    })
    const data =await res.json()
    setName("")
    setDate("")
    setDescription("")
    console.log(data)
  }

  return (
    <main>
      <div className="container">
        <form onSubmit={addNewTransaction}>
          <h1 className="appHeading">MoneyTracker</h1>

          <div className="basicInfo">
            <input
              type="text"
              placeholder="-20000 samsung tv"
              value={name}
              onChange={(ev) => setName(ev.target.value)}
            />
            <input
              type="datetime-local"
              placeholder="time"
              value={Date}
              onChange={(ev) => setDate(ev.target.value)}
            />
          </div>
          <div>
            <input
              type="text"
              placeholder="description"
              value={description}
              onChange={(ev) => setDescription(ev.target.value)}
            />
          </div>
          <button>Add new transaction</button>
          {transactions.length}
        </form>
        {transactions.map(transaction =>{
          <div className="transactions">
          <div className="transaction">
            <div className="left">
              <div className="transactionName">{transaction.name}</div>
              <div className="transactionDescription">{transaction.description}</div>
            </div>
            <div className="right">
              <div className="transactionPrice red">{`${transaction.price}₹`}</div>
              <div className="transactionDate">{transaction.Date}</div>
            </div>
          </div>

        </div>})}
      </div>
    </main>
  );
}

export default App;
